import React, { useState, useEffect } from "react";
import axios from "axios";

function App() {
  const [data, setData] = useState("");
  const [location, setLocation] = useState("Hong Kong");
  const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=ccdf5d37eb541fa8231e89bf99da5797`;
  const icon = `http://openweathermap.org/img/wn/${data.weather[0].icon}@2x.png`;
  useEffect(() => {
    setLocation("Hong Kong");
    axios.get(url).then((response) => {
      setData(response.data);
      console.log(response.data);
    });
    setLocation("");
  }, []);

  const searchLocation = (event) => {
    if (event.key === "Enter") {
      axios.get(url).then((response) => {
        setData(response.data);
        console.log(response.data);
      });
      setLocation("");
    }
  };

  return (
    <div className="app">
      <div className="search">
        <input
          type="text"
          value={location}
          onChange={(event) => {
            setLocation(event.target.value);
          }}
          onKeyPress={searchLocation}
          placeholder="Enter City name"
        />
      </div>
      <div className="container">
        <div className="top">
          <div className="location">
            <p>{data.name}</p>
          </div>
          <div className="temp">
            {data.main ? (
              <h1>{(data.main.temp - 272.15).toFixed(1)} °C</h1>
            ) : null}
          </div>
          <div className="description">
            {data.weather ? <p>{data.weather[0].main}</p> : null}
          </div>
          <div className="icon">
            <img style={{ width: "5rem" }} src={icon} alt="" />
          </div>
        </div>
      </div>
      <div className="bottom">
        <div className="feels">
          {data.main ? (
            <p className="bold">
              {(data.main.feels_like - 272.15).toFixed(1)} °C
            </p>
          ) : null}

          <p>Feels like</p>
        </div>
        <div className="humidity">
          {data.main ? <p className="bold">{data.main.humidity} %</p> : null}
          <p>Humidity</p>
        </div>
        <div className="wind">
          {data.wind ? <p className="bold">{data.wind.speed} MPH</p> : null}
          <p>Wind Speed</p>
        </div>
      </div>
    </div>
  );
}

export default App;
