import React, { useState } from "react";
import axios from "axios";

function App() {
  const [data, setData] = useState("Hong Kong");
  const [location, setLocation] = useState("");
  const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=ccdf5d37eb541fa8231e89bf99da5797`;

  const searchLocation = (event) => {
    if (event.key === "Enter") {
      axios.get(url).then((response) => {
        setData(response.data);
        console.log(response.data);
      });
      setLocation("");
    }
  };

  return (
    <div className="app">
      <div className="search">
        <input
          type="text"
          value={location}
          onChange={(event) => {
            setLocation(event.target.value);
          }}
          onKeyPress={searchLocation}
          placeholder="Enter City name"
        />
      </div>
      <div className="container">
        <div className="top">
          <div className="location">
            <p>{data.name}</p>
          </div>
          <div className="temp">
            {data.main ? (
              <h1>{(data.main.temp - 272.15).toFixed(1)} Â°C</h1>
            ) : null}
          </div>
          <div className="description">
            {data.weather ? <p>{data.weather[0].main}</p> : null}
          </div>
        </div>
      </div>
      <div className="bottom">
        <div className="feels">
          {data.main ? (
            <p className="bold">
              {(data.main.feels_like - 272.15).toFixed(1)}"C
            </p>
          ) : null}

          <p>Feels like</p>
        </div>
        <div className="humidity">
          <p className="bold">20%</p>
          <p>Humidity</p>
        </div>
        <div className="wind">
          <p className="bold">12 MPH</p>
          <p>Wind Speed</p>
        </div>
      </div>
    </div>
  );
}

export default App;
